import React, { useState, useEffect, useContext } from "react";
import "./pDashboard.css";
import Navbar from "../Common/Navbar";
import { AuthContext } from "../../context/AuthContext";
import SpeechRecognition from "../sample/SpeechRecognition";
import DemoVideos from "../sample/DemoVideos";
import EditProfileModal from "../sample/EditProfileModal";
import { Button } from "@mui/material";
import Reminder from "../Patient/Reminder";
import SpeechTherapyDemo from "../sample/SpeechTherapyDemo";
const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [message, setMessage] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [therapistId, setTherapistId] = useState("");
  const [submissionStatus, setSubmissionStatus] = useState(null);
  const [patientPlan, setPatientPlan] = useState([]);
  const [isEditProfileOpen, setIsEditProfileOpen] = useState(false);
  const [openNotifications, setOpenNotifications] = useState(false);

  useEffect(() => {
    console.log("User context:", user);
  }, [user]);

  const patientId = user ? user.id : null; // Get patient ID from user context

  // Fetch patient plan data
  useEffect(() => {
    console.log("useEffect triggered with patientId:", patientId);
    if (patientId) {
      const fetchPatientPlan = async () => {
        try {
          console.log(`Fetching patient plan for patient ID: ${patientId}`);
          const response = await fetch(`/get-patient-plan/${patientId}`);
          if (!response.ok) {
            throw new Error("Failed to fetch patient plan");
          }
          const data = await response.json();
          console.log("Fetched patient plan data:", data); // Log the fetched data
          setPatientPlan(data);
        } catch (error) {
          console.error("Error fetching patient plan:", error);
        }
      };

      fetchPatientPlan();
    }
  }, [patientId]);

  const handleSubmitAppointment = async () => {
    try {
      const response = await fetch(`/create-appointment`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          patient_id: patientId,
          therapist_id: therapistId,
          date: appointmentDate,
          time: appointmentTime,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      alert(data.message || "Appointment submitted successfully!");
      setSubmissionStatus("success");

      // Clear form after submission
      setAppointmentDate("");
      setAppointmentTime("");
      setTherapistId("");
    } catch (error) {
      console.error("Error submitting appointment:", error);
      alert("Failed to submit appointment. Please check the form and try again.");
      setSubmissionStatus("error");
    }
    const handleEditProfileClick = () => {
      console.log("Edit Profile button clicked");
      setIsEditProfileOpen(true);
  };
  
  };

  return (
    <>
      <Navbar />
      
      <div className="dashboard-container">
        <header className="dashboard-header">
          <h2>WELCOME BACK, {user ? user.full_name : "USER_NAME"}</h2>
        </header>

        <nav className="dashboard-nav">
          
          <Reminder open={openNotifications} onClose={() => setOpenNotifications(false)} />
          <button>MY GOALS</button>
          <button onClick={() => setIsEditProfileOpen(true)}>EDIT PROFILE</button>
          <EditProfileModal isOpen={isEditProfileOpen} onClose={() => setIsEditProfileOpen(false)} />
        </nav>

        <section className="services">
          <h3>SERVICES OFFERED</h3>
          <div className="service-cards">
            <div className="service">
              <img src="data:image/jpeg;base64," alt="Therapy" />
              <p>THERAPY</p>
            </div>
            <div className="service">
              <img src="data:image/jpeg;base64," alt="Mental Wellness" />
              <p>Speech therapists</p>
            </div>
            <div className="service">
              <img src="data:image/jpeg;base64," alt="Meditation" />
              <p>One to one interactions</p>
            </div>
          </div>
        </section>

        {/* Updated Current Plans Section */}
        <section className="current-plans">
          <h3>CURRENT PLANS</h3>
          <div className="plans-container">
            {patientPlan.length > 0 ? (
              patientPlan.map((plan, index) => (
                <div key={index} className="plan">
                  <p><strong>Week 1:</strong> {plan.week1 || "No activities"}</p>
                  <p><strong>Week 2:</strong> {plan.week2 || "No activities"}</p>
                  <p><strong>Week 3:</strong> {plan.week3 || "No activities"}</p>
                  <p><strong>Week 4:</strong> {plan.week4 || "No activities"}</p>
                </div>
              ))
            ) : (
              <p>Loading patient plan...</p>
            )}
          </div>
        </section>

        <section className="appointment">
          <h3>REQUEST APPOINTMENT</h3>
          <div className="appointment-form">
            <input
              type="date"
              value={appointmentDate}
              onChange={(e) => setAppointmentDate(e.target.value)}
            />
            <input
              type="time"
              value={appointmentTime}
              onChange={(e) => setAppointmentTime(e.target.value)}
            />
            <input
              type="number"
              placeholder="Therapist ID"
              value={therapistId}
              onChange={(e) => setTherapistId(e.target.value)}
            />
            <button onClick={handleSubmitAppointment}>Submit</button>
          </div>

          {submissionStatus === "success" && <p style={{ color: "green" }}>Appointment submitted successfully!</p>}
          {submissionStatus === "error" && <p style={{ color: "red" }}>Failed to submit appointment. Please try again.</p>}
        </section>
            {/* <DemoVideos /> */}
            <SpeechTherapyDemo />
            <section className="appointment-form">
              <SpeechRecognition />
            </section>
            
        <section className="feedback">
          <h3>FEEDBACK</h3>
          <textarea
            placeholder="Type your message here..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button>Send</button>
        </section>
      </div>
      

    </>
  );
};

export default UserDashboard;

